SELECT UPPER(LatinName) AS UppercaseLatinName, CommonName, COUNT(*) AS TotalTrees, SUM(CASE WHEN Year = 1995 THEN AliveTrees ELSE 0 END) AS TreesAlive_1995, SUM(CASE WHEN Year = 1995 THEN DeadTrees ELSE 0 END) AS TreesDead_1995, SUM(CASE WHEN Year = 2015 THEN AliveTrees ELSE 0 END) AS TreesAlive_2015, SUM(CASE WHEN Year = 2015 THEN DeadTrees ELSE 0 END) AS TreesDead_2015, (SUM(CASE WHEN Year = 2015 THEN AliveTrees ELSE 0 END) - SUM(CASE WHEN Year = 1995 THEN AliveTrees ELSE 0 END)) AS GrowthInCount