SELECT DATE(transaction_timestamp) AS date, SUM(CASE WHEN input LIKE '%0x40c10f19%' THEN CAST(SUBSTRING(input, 7, 64) AS DECIMAL(38, 0)) / 1000000 ELSE 0 END) - SUM(CASE WHEN input LIKE '%0x42966c68%' THEN CAST(SUBSTRING(input, 7, 64) AS DECIMAL(38, 0)) / 1000000 ELSE 0 END) AS daily_change FROM ethereum_transactions WHERE address = '0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48' AND YEAR(transaction_timestamp) = 2023 GROUP BY date ORDER BY date DESC