SELECT COUNT(*) AS NumericValue FROM ( SELECT DISTINCT T1.CouncilDistrict AS District FROM BikeTrips AS T1 INNER JOIN Stations AS T2 ON T1.StartStation = T2.StationID INNER JOIN Stations AS T3 ON T1.EndStation = T3.StationID WHERE T1.CouncilDistrict IS NOT NULL AND T2.StationID != T3.StationID GROUP BY T1.CouncilDistrict ORDER BY COUNT(DISTINCT T2.StationID) DESC LIMIT 1 ) AS ActiveCouncilDistrict;