SELECT b.name, m.match_id, o.runs_conceded FROM bowler b JOIN match m ON b.match_id = m.match_id JOIN over o ON b.over_id = o.over_id WHERE o.max_runs_conceded IN (SELECT MAX(max_runs_conceded) FROM (SELECT b.bowler_id, m.match_id, SUM(o.runs_conceded) as max_runs_conceded FROM bowler b JOIN match m ON b.match_id = m.match_id JOIN over o ON b.over_id = o.over_id GROUP BY b.bowler_id, m.match_id HAVING COUNT(*) > 1)