SELECT b.bowler_name, m.match_number, g.game_number, b.handicap_score, t.tournament_date, l.location FROM bowler b JOIN match m ON b.id = m.bowler_id JOIN game g ON m.id = g.match_id JOIN tournament t ON g.tournament_id = t.id JOIN lane l ON g.lane_id = l.id WHERE b.won AND b.handicap_score <= 190 AND (l.name = 'Thunderbird Lanes' OR l.name = 'Totem Lanes' OR l.name = 'Bolero Lanes');